import React, { useEffect, useState } from "react";
import './App.css'

function ArticleLists(props){
    const [articleList, setArticleList] = useState({
        data : 
            {
                id: 'DEFAULT',
                title: 'DEFAULT',
                created_id: 'DEFAULT',
                created_at: 'DEFAULT',
                category_name: 'DEFAULT',
                visit_cnt: 'DEFAULT',
                comment_cnt: 'DEFAULT'
            }
    });

    
    useEffect(()=> {
        setArticleList(props.ArticleLists);
        console.log(articleList);
        console.log(articleList.data[0]);
    },[props.ArticleLists, articleList])

    return (
        <div>
            Board Data(Text â†’ JSON) :
            <div>
                {(articleList.data).map((article, index) => (
                    <Article data={article} index={index} />
                ))}
            </div>
            {/* <Article list={articleList.data}/> */}
        </div>
    )
}

function Article({data, index}) {
    return (
        <ul id="ulNone" >
            <li key={index}>
                <b>ID : </b> <span key={index}>{data.id}</span> <br/>
                <b>Title : </b> <span key={index}>{data.title}</span> <br/>
                <b>Category : </b> <span key={index}>{data.category_name}</span> <br/>
                <b>Created By : </b> <span key={index}>{data.created_id}</span> <br/>
                <b>Created At : </b> <span key={index}>{data.created_at}</span> <br/>
                <b>Visit : </b> <span key={index}>{data.visit_cnt}</span> <br/>
                <b>Comment : </b> <span key={index}>{data.comment_cnt}</span> <br/>
            </li>
        </ul>
    );
}

export default ArticleLists;
