import React, { useEffect, useState } from "react";
import './App.css'

function Article({articleList}) {
    const getEntries = Object.entries(articleList.data).map((entry, idx)=>{
        console.log(entry);
        return entry;
    })

    return (
      <div>
        {/* article만 불러오면 출력이 안됨, .data 또는 .id 등 붙이면 못읽음
            articleList에 값이 아직 없는 상태에서 출력하려고 하는 것 같음 */}
        {/* <b>ID : </b> <span>{article.data(0).id}</span> <br/> */}
        <b>ID : </b> <span>{getEntries.values}</span> <br/>
        {/* <b>Title : </b> <span>{article}</span> <br/>
        <b>Category : </b> <span>{article}</span> <br/>
        <b>Created By : </b> <span>{article}</span> <br/>
        <b>Created At : </b> <span>{article}</span> <br/>
        <b>Visit : </b> <span>{article}</span> <br/>
        <b>Comment : </b> <span>{article}</span> <br/> */}
      </div>
    );
}


function ArticleLists(props){
    const [articleList, setArticleList] = useState({
        data : 
            {
                id: 'DEFAULT',
                title: 'DEFAULT',
                created_id: 'DEFAULT',
                created_at: 'DEFAULT',
                category_name: 'DEFAULT',
                visit_cnt: 'DEFAULT',
                comment_cnt: 'DEFAULT'
            }
    });

    
    useEffect(()=> {
        // let resData = null;
        // async function fetchData() {
        //     resData = await props.ArticleLists;
        // }
        // fetchData();
        setArticleList(props.ArticleLists);
        // console.log(JSON.stringify(props));
        console.log(articleList);
        console.log(articleList.data[0]);
    },[props.ArticleLists, articleList])

    
    
    return (
        <div>
            Board Data(Text → JSON) :
            {/* <div>
                <b>ID : </b> <span>{articleList.data[0]}</span> <br/>
                <b>Title : </b> <span>{articleList.data[0]}</span> <br/>
                <b>Category : </b> <span>{articleList.data[0]}</span> <br/>
                <b>Created By : </b> <span>{articleList.data[0]}</span> <br/>
                <b>Created At : </b> <span>{articleList.data[0]}</span> <br/>
                <b>Visit : </b> <span>{articleList.data[0]}</span> <br/>
                <b>Comment : </b> <span>{articleList.data[0]}</span> <br/>
            </div> */}
            {/* {getEntries} */}
            <Article list={getEntries}/>
        </div>
    )
}

export default ArticleLists;
