import { useState, useCallback, useEffect } from "react";
import _, { split } from "lodash";
import { Link } from "react-router-dom";
import { Fetching, isEmpty, username } from "../func";
import axios from "axios";
import userEvent from "@testing-library/user-event";
import Article from "../Article/Article";

function MyPage(){
    const [visible, setVisible] = useState(false);
    const [visibleArticle, setVisibleArticle] = useState(false);
    const [visibleComment, setVisibleComment] = useState(false);
    const user = Fetching("user", username());

    if(!user) { return <div> Loading ... </div> }
    else {
        return (
            <div id="div-box">
                <h1>MY PAGE</h1><hr/>
                <div style={{textAlign: "left", border: "1px solid black", margin: "20px"}}>
                    <b> Name : </b><span> {user.name} </span><br/>
                    <b> Email : </b><span> {user.username} </span><br/>
                    <b> Nickname : </b><span> {user.nick_name} </span><br/>
                    <b> PhoneNumber : </b><span> {user.phone} </span><br/><br/>


                    <b> ROLE : </b><span> {split(user?.authorities[0]?.authority, "_")[1]} </span><br/>
                    <button id="btn-post" style={{position: "absolute", top: "115px", right: "30px"}}
                            onClick={() => setVisible(!visible)}> {visible ? "Cancel" : "Change PW"} </button>
                    {visible && <PasswordEditForm auth={user?.authorities[0]?.authority}/>}
                </div>
                {/* <div>
                    <button id="btn-default" onClick={() => setVisibleArticle(!visibleArticle)}> My Articles </button>
                    {visibleArticle && <MyArticles user={user} />}
                </div>
                <div>
                    <button id="btn-default" onClick={() => setVisibleComment(!visibleComment)}> My Comments </button>
                    {visibleArticle && <MyComments user={user}/>}
                </div> */}
                <Link to={`/`} id="none"> <button id="btn-default"> Home </button></Link>
                <Link to={`/board`} id="none"> <button id="btn-default"> Board </button></Link>
            </div>
        )
    }
}

// 작성한 글 목록, 댓글 목록

function MyArticles({user}){
    const [articles, setArticles] = useState({data : {}});
    useEffect(() => {
        axios.get(`/user/article/${user?.nick_name}`).then((res) => {
            setArticles(res?.data);
        }).catch((e) => {
            alert("Error : " + e.response.statusText + "\nReturn to Board");
            window.location.replace("/board");
        });
    }, []);

    return (
        <div>
            {articles?.map((article, index) => {
                <Article key={index} data={article} auth={user?.auth} />
            })}
        </div>
    )
}

function MyComments(){
    const [comments, setComments] = useState({data : {}});
    useEffect(() => {
        axios.get(`/user/comment/${user?.nick_name}`).then((res) => {
            setArticles(res?.data);
        }).catch((e) => {
            alert("Error : " + e.response.statusText + "\nReturn to Board");
            window.location.replace("/board");
        });
    }, []);

    return (
        <div>
            {articles?.map((article, index) => {
                <Article key={index} data={article} auth={user?.auth} />
            })}
        </div>
    )
}

function PasswordEditForm({auth}) {
    const [newPassword, setNewPassword] = useState("");
    const [checkPassword, setCheckPassword] = useState("");
    
    const inputPassword = useCallback( e => {
        setNewPassword(e.target.value);
    }, [])
    
    const checkNewPassword = useCallback( e => {
        setCheckPassword(e.target.value);
    }, [])

    const changePW = (e) => {
        if(isEmpty(newPassword)){ 
            alert("You must input new password");
            return Error;
        }

        if(isEmpty(checkPassword)){ 
            alert("You must check your password");
            return Error;
        }

        if(!_.isEqual(newPassword, checkPassword)){
            alert("Not matching password.\nTry again.")
            return Error;
        }

        axios.put(`/user/${username()}`, {
            data : {
                email : username(),
                password : newPassword,
                auth : auth
            }
        }).then(() => {
            alert("Password Changed.\nPlease re-login.");
            sessionStorage.removeItem('username');
            window.location.replace(`/login`);
        }).catch((e) => {
            alert("Failed to change password.\nPlease try again.");
            window.location.replace("/mypage");
        })
    }
    if(isEmpty)

    return (
        <div id="div-box">
            <br/>
            <form onSubmit={changePW}>
                <b> Change password </b><br/>
                <input type={"password"} placeholder="New Password" onChange={inputPassword}></input><br/>
                <input type={"password"} placeholder="Check Password" onChange={checkNewPassword}></input><br/>
                <button type="submit" id="btn-post"> change </button>
            </form>
        </div>
    )
}

export default MyPage;
