import React, {useState, useEffect} from "react";
import {Link} from 'react-router-dom';

function ArticleDeatil(props){
    
    const [articleList, setArticleList] = useState({
        data:{}
    })
    
    
    const [articleDetail, setArticleDetail] = useState({
        data : {}
    });
    
    const callArticle = async () => {
        const RES = await fetch(`/board/${articleId}`)
        .then((res) => res.json());
        return setArticleDetail({articleDetail:RES});
    }
    
    useEffect( ()=> {
        setArticleList(props.ArticleDeatil);
        return callArticle;
    }, []);
    
    const articleId = articleList;
    console.log(articleList);

    const articleDetailArr = Array.from(articleDetail.data);

    return (
        <div>
            {articleDetailArr.map((article, index) => (
                <ArticleDetailData data={article} index={index} />
            ))}
        </div>
    )
}


function ArticleDetailData({data, index}) {

    return (
        <li id="liNone"> Article Detail : 
            <b> ID : </b> <span> {data.id} </span> <br/>
            <b> Title : </b> <span> {data.title} </span> <br/>
            <b> Category : </b> <span> {data.category_name} </span> <br/>
            <b> Content : </b> <span> {data.content} </span> <br/>
            <b> Created By : </b> <span> {data.created_id} </span> <br/>
            <b> Created At : </b> <span> {data.created_at} </span> <br/>
            <b> Visit : </b> <span> {data.visit_cnt} </span> <br/>
            <li id="liNone"><b> Comment : </b> <span> 
                    <b> Comment ID : </b> <span> {data.comment.id} </span>
                    <b> Content : </b> <span> {data.comment.content} </span>
                    <b> Created At : </b> <span> {data.comment.created_at} </span>
                </span> <br/></li>
            <nav>
                <Link to={`/`}>
                    <button id="btn-detail"> Home </button></Link>
            </nav>
        </li>
    );
}


export default ArticleDeatil;
