import {Link} from 'react-router-dom';
import ArticleList from './ArticleList';
import '../App.css'
import { FetchWithoutId } from '../func';
import { useState } from 'react';
import _ from 'lodash';
import Pagination from 'react-js-pagination';

function ArticleLists({user, isLogin}){
    const [limit, setLimit] = useState(10);
    const [currentPage, setCurrentPage] = useState(1);
    
    const articleList = Array.from(FetchWithoutId("board").data);

    
    if(_.isEmpty(articleList)){ return <div> Loading... </div> }
    else {
        return (
            <> 
                <b style={{ fontSize: "30px", margin : "10px"}}> Article List </b>
                {isLogin && 
                    <Link to={`/board/add/0`} id="none"> 
                        <button className="w3-button w3-border w3-round-xlarge w3-small w3-hover-teal"> 
                                Write article </button></Link>}
                <ArticleList user={user} articleList={articleList} limit={limit} currentPage={currentPage}/> <hr/>
                
                <Page total={articleList.length} limit={limit} page={currentPage} setPage={setCurrentPage} />
                <Paging articleList={articleList} page={currentPage} setPage={setCurrentPage} />
            </>
        )
    }
}

function Page({ total, limit, page, setPage }) {
    const numPages = Math.ceil(total / limit);
  
    return (
      <div id='div-box'>
            <button className='w3-button w3-round-xxlarge w3-small w3-hover-gray'
                  onClick={() => setPage(page - 1)} disabled={page === 1}> {"<"} </button>
            {Array(numPages).fill().map((_, i) => (
                <button key={i + 1} onClick={() => setPage(i + 1)} aria-current={page === i + 1 ? {className:'w3-button w3-round-xxlarge w3-small w3-color-deep-purple'} : null}
                        className='w3-button w3-round-xxlarge w3-small w3-hover-deep-purple'> {i + 1} </button>
            ))}
          <button className='w3-button w3-round-xxlarge w3-small w3-hover-gray'
                  onClick={() => setPage(page + 1)} disabled={page === numPages}> {">"} </button>
      </div>
    );
}

function Paging({articleList, page, setPage}) {;
  
    const handlePageChange = (page) => {
      setPage(page);
    };
  
    return (
        <Pagination 
            activePage={page} // 현재 페이지
            itemsCountPerPage={10} // 한 페이지랑 보여줄 아이템 갯수
            totalItemsCount={articleList.length} // 총 아이템 갯수
            pageRangeDisplayed={5} // paginator의 페이지 범위
            prevPageText={"‹"} // "이전"을 나타낼 텍스트
            nextPageText={"›"} // "다음"을 나타낼 텍스트
            onChange={handlePageChange} // 페이지 변경을 핸들링하는 함수
            activeClass={"w3-button w3-round-xxlarge w3-small w3-color-deep-purple"}
        />
    );
  };

export default ArticleLists;
