import React, {Component} from "react";
import ArticleLists from "./articleList";
import './App.css';

class App extends Component{
    constructor(props){
        super(props);
        this.state = {
                data: [
                // {
                //     id: '',
                //     title: '',
                //     created_id: '',
                //     created_at: '',
                //     category_name: '',
                //     visit_cnt: '',
                //     comment_cnt: ''
                // },
            ]
        }
    }

    componentDidMount() {
        this.callApi();
    }

    responseHandler = ({data}) => {
        this.setState(data);
        return data;
    };

    callApi = async () => {
        const RES = await (await fetch('/board'))
                                    // .then(responseHandler)            
                                    .json();
        console.log(RES);
        return this.setState({data: RES.data});
    }

    render(){
        return(
            <div className="App">
                <ArticleLists ArticleLists={this.state.articleList} />
            </div>
        );
    }
}

export default App;
