import { Link } from "react-router-dom";
import {useState, useCallback} from 'react';
import axios from "axios";
import * as Function from "../func"
import "../App.css";

function LoginForm(){
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const inputEmail = useCallback(e => {
        setEmail(e.target.value);
    }, [])

    const inputPassword = useCallback(e => {
        setPassword(e.target.value);
    }, [])

    const Login = (e) => {
        e.preventDefault();

        if(Function.isEmpty(email)){
            alert("You must input your Email");
            return Error;
        }else{setEmail(email);}

        if(Function.isEmpty(password)){
            alert("You must input password");
            return Error;
        }else{setPassword(password);}

        let userData = {
            data : {
                email : email,
                password : password
            }
        }

        axios.get('/userlogin', JSON.stringify(userData), {
            headers: {
                "Content-Type": `application/json`
            }
        });
        console.log(email + "," + password)
        
        alert("Welcome back!");
    }

    return (
        <div className="container">
            <br/>
            <form className="form-signin" onSubmit={Login}>
                <h2 className="form-signin-heading">Please sign in</h2>
                <p>
                    <label htmlFor="username" className="sr-only">User ID</label>
                    <input type="text" id="username" name="username" className="form-control" placeholder="ID" onChange={inputEmail}/>
                </p>
                <p>
                    <label htmlFor="password" className="sr-only">Password</label>
                    <input type="password" id="password" name="password" className="form-control" placeholder="PW" onChange={inputPassword}/>
                </p>
                    {/* <input name="_csrf" type="hidden" value="af226a34-6934-4e3b-b60d-dfe50903a34a" /> */}
                    <button className="btn btn-lg btn-primary btn-block" type="submit">Sign in</button>
                    <Link to="/login/signup"><button className="btn btn-lg btn-primary btn-blcok" type="submit">Sign up</button></Link>
            </form>
        </div>
    )
}

export default LoginForm;
