import { Link } from "react-router-dom";
import {useState, useCallback} from "react";
import axios from "axios";
import "../App.css";

function LoginForm(){
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const inputEmail = useCallback(e => {
        setEmail(e.target.value);
    }, [])

    const inputPassword = useCallback(e => {
        setPassword(e.target.value);
    }, [])

    const login = (e) => {
        e.preventDefault();
        if(Function.isEmpty(email)){
            alert("You must input your Email");
            return Error;
        }else{setEmail(email);}

        if(Function.isEmpty(password)){
            alert("You must input password");
            return Error;
        }else{setPassword(password);}

        let userData = {
            data : {
                email : email,
                password : password
            }
        }
        
        axios.post('/login', JSON.stringify(userData), {
            headers: {
                "Content-Type": `application/json`
            }
        }).then(() => {
            sessionStorage.setItem("username", "lana@gmail.com");
            console.log(sessionStorage.getItem("username"));
        }).catch((e) => {
            alert("Error. Please try again.\n" + e.response);
            window.location.href="/login/signup";    
        });
        
        if(e){
            alert("Register Successed.\nPlease login.");
            window.location.href="/login";
        }
    }

    return (
        <div id="div-box">
        <br/>
        <form onSubmit={login}>
            <h2 >Please Login</h2> <hr/>
            <input type="text" id="username" name="username" onChange={inputEmail} placeholder="ID (Email)" required autoFocus/><br/>
            <input type="password" id="password" name="password" onChange={inputPassword} placeholder="Password" required/><br/>
            <button id="btn-post" type="submit">Login</button>
            <Link to="/login/signup"><button id="btn-default" type="submit">Sign up</button></Link>
        </form> 
        <Link id="none" to="/"><button id="btn-default">Home</button></Link>
    </div>
    )
}

export default LoginForm;
