import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";
import "../App.css";

function LoginForm(){
    const [account, setAccount] = useState({
        username: "",
        password: "",
    });

    //input에 입력될 때마다 account state값 변경되게 하는 함수
    const onChangeAccount = (e) => {
        setAccount({
        ...account,
        [e.target.name]: e.target.value,
        });
    };

    function userlogin(){
        axios.post('/userlogin', account)
        .then((res) => console.log(res))
        .catch((e) => {
            console.log(e.response.status + " : " + e.response.statusText)
        })
    }

    return (
    <div id="div-box">
        <br/>
        <form action="/userlogin" method="post">
            <h2 >Please Login</h2> <hr/>
            <input type="text" id="username" name="username" placeholder="ID (Email)" onChange={onChangeAccount} required autoFocus/><br/>
            <input type="password" id="password" name="password" placeholder="Password" onChange={onChangeAccount} required/><br/>
            <button id="btn-post" type="submit">Login</button>
            <Link to="/login/signup"><button id="btn-default">Sign up</button></Link>
        </form> 
    </div>
    )
}

export default LoginForm;
