import "../App.css";
import axios from "axios";
import { useEffect, useState, useCallback } from "react";
import { Link } from "react-router-dom";

function LoginForm(){

    const [id, setId] = useState("");
    const [pw, setPW] = useState("");

    const [msg, setMsg] = useState(""); 

    useEffect(()=> {
        if(msg) {
            setTimeout(() => {
                setMsg("");
            }, 1500);
        }
    }, [msg])

    const inputId = useCallback(e => {
        setId(e.target.value);
    }, [])
    const inputPw = useCallback(e => {
        setPW(e.target.value);
    }, [])

    function login(e) {
        e.preventDefault();
        if (!id) {
            return alert("Input Id");
        } else {
            setId(e.target.value);
        }

        if (!pw) {
            return alert("Input PW");
        } else {
            setPW(e.target.value);
        }
        
        axios.get("/login", {
                    data: {
                        id: id,
                        pw: pw,
                    }
                }).then((res) => {
                switch (res.data.code) {
                    case 200:
                        setMsg("");
                        break;
                    case 400:
                        setMsg("ID, PW is Empty");
                        break;
                    case 401:
                        setMsg("Not existing ID");
                        break;
                    case 402:
                        setMsg("Wrong Password");
                        break;
                    default:
                        break;
                }
            });
    }

    return (
        <div class="container">
            <br/><br/>
            <form class="form-signin" method="post" action="/login">
                <h2 class="form-signin-heading">Please sign in</h2>
                <p>
                    <label for="username" class="sr-only">User ID</label>
                    <input type="text" id="username" name="username" class="form-control" placeholder="ID" required autofocus/>
                </p>
                <p>
                    <label for="password" class="sr-only">Password</label>
                    <input type="password" id="password" name="password" class="form-control" placeholder="PW" required/>
                </p>
                    <input name="_csrf" type="hidden" value="af226a34-6934-4e3b-b60d-dfe50903a34a" />
                    <button class="btn btn-lg btn-primary btn-block" type="submit">Sign in</button>
            </form>

            {/* <form onSubmit={login}>
                <div id="div-box">
                    <b style={{textAlign: "center"}}> Login Page </b> <br/>
                    <input placeholder="ID" onChange={inputId}></input> <br/>
                    <input type="password" placeholder="Password" onChange={inputPw}></input> <br/>
                    <button type="submit" id="btn-post" style={{textAlign: "right"}}
                            onClick={() => {window.location.href=`/board`}}> Login </button>
                    <Link id="none" to="/"><button id="btn-default"> Home </button></Link>
                </div>
            </form>
            <div style={{textAlign: "center"}}>
                <b> Don't have id ? </b>
                <Link id="none" to="/login/signup"><button id="btn-default"> Sign up </button></Link>
            </div> */}
        </div>
    )
}

export default LoginForm;
