import {Link} from 'react-router-dom';
import * as Function from "../func";
import '../App.css'

function CategoryBar(){
    const categoryList = Function.Fetching("category", 1);
    const user = Function.Fetching('user', sessionStorage.getItem('username'));
    // function isAdmin(){
