import {Link} from 'react-router-dom';
import ArticleList from './ArticleList';
import '../App.css'
import { FetchWithoutId } from '../func';
import { useState } from 'react';
import { ReactPaginate, Items } from 'react-paginate';
import _ from 'lodash';
import { Label } from 'reactstrap';
import { off } from 'process';
import Article from './Article';

function ArticleLists({user, isLogin}){
    const [limit, setLimit] = useState(5);
    const [currentPage, setCurrentPage] = useState(1);
    const [articlesPerPage, setArticlesPerPage] = useState(new Set());
    const offset = (currentPage - 1) * limit;
    
    const articleList = Array.from(FetchWithoutId("board").data);
    const pages = Math.ceil(articleList.length / 5);

    // const articles = () => {
    //     articleList.map((article, index) => {
    //         if(index % pageRange===1){
    //             articlesPerPage.clear();
    //             setCount(count + 1);
    //         }
    //         articlesPerPage.add(article);
    //     })
    //     return setArticlesPerPage(articlesPerPage);
    // }
    // console.log("Pages : " + pages);
    // console.log("CurrentPage : " + currentPage);
    
    // for(var i = 0; i < articlesPerPage.size ; i++){
    //     console.log("articles per page : " + articlesPerPage[i]);
    // }
    
    if(_.isEmpty(articleList)){ return <div> Loading... </div> }
    else {
        return (
            <> 
                <b style={{ fontSize: "30px", margin : "10px"}}> Article List </b>
                {isLogin && 
                    <Link to={`/board/add/0`} id="none"> 
                        <button className="w3-button w3-border w3-round-xlarge w3-small w3-hover-teal"> 
                                Write article </button></Link>}
                {/* <ArticleList user={user} articleList={articleList} /> <hr/> */}

                {/* <Paging posts={articleList}/> */}
                <Label>
                    <select
                        type="number"
                        value={limit}
                        onChange={({target: {value} }) => setLimit(Number(value))}
                    >
                        <option value={"10"}>10</option>
                        <option value={"20"}>20</option>
                        <option value={"50"}>50</option>
                        <option value={"100"}>100</option>
                    </select>
                </Label>
                {articleList.slice(offset, offset + limit).map((article, index) => {
                    return (
                        <tr id="clickable" key={index}>
                            {/* {isAdmin(user.auth) && 
                                <input  type={'checkbox'} onChange={(e) => {checkHandler(e, article.id);}}
                                name="check" value={article.id}
                                style={{width: "20px", height: "20px", margin: "11px"}}/>
                            } */}
                            <Article data={article} auth={user?.auth}/>
                        </tr>
                    )
                })}
                <Pagination total={articleList.length} limit={limit} page={currentPage} setPage={setCurrentPage} />
                <div id='div-box'>
                    <button className='w3-button w3-round-xxlarge w3-small we-hover-gray'> {'<<'} </button>
                    <button className='w3-button w3-round-xxlarge w3-small w3-hover-deep-purple'>1</button>
                    <button className='w3-button w3-round-xxlarge w3-small w3-hover-deep-purple'>2</button>
                    <button className='w3-button w3-round-xxlarge w3-small w3-hover-deep-purple'>3</button>
                    <button className='w3-button w3-round-xxlarge w3-small we-hover-gray'> {'>>'} </button>
                </div>
            </>
        )
    }
}

function Pagination({ total, limit, page, setPage }) {
    const numPages = Math.ceil(total / limit);
  
    return (
      <>
        <Link>
          <button onClick={() => setPage(page - 1)} disabled={page === 1}>
            &lt;
          </button>
          {Array(numPages)
            .fill()
            .map((_, i) => (
              <button
                key={i + 1}
                onClick={() => setPage(i + 1)}
                aria-current={page === i + 1 ? "page" : null}
              >
                {i + 1}
              </button>
            ))}
          <button onClick={() => setPage(page + 1)} disabled={page === numPages}>
            &gt;
          </button>
        </Link>
      </>
    );
  }

export default ArticleLists;
