import axios from "axios";
import { useState, useEffect } from "react";

export function isEmpty(value){
    if(value === "" || value === null || value === undefined || ( value !== null && typeof value === "object" && !Object.keys(value).length)){
        return true;
    }else { return false; }
}

export function isLogin(member){
   if(member.data){ return true; }
   else { return false; }
}

export function getUrlId(){
    const urlList = ((window.location.href).split('/'));
    const id = urlList[(urlList.length)-1];
    return id;
}

export function Fetching(dataName){
    const id = getUrlId();
    console.log(id);

    const [data, setData] = useState({ data : {} });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if(isNaN(id)){
            console.log(dataName);
            axios.get(`/${dataName}`).then((res) => {
                setData(res?.data);
                setLoading(false);
            });
        }else{
            console.log(dataName + " , " + id);
            axios.get(`/${dataName}/${id}`).then((res) => {
                setData(res?.data);
                setLoading(false);
            });
        }
    }, []);
    if(!loading) { return data.data; }
}
