import React from "react";
import ArticleDetail from "./ArticleDetail";

function ArticleEditForm() {
    const articleId = ArticleDetail.articleId;

    return(
        <div>
            <br/><br/><br/>
            <form onSubmit={ArticleDetail.editArticle}>
                <div id="div-box">
                    <b style={{textAlign: "center"}}> Editing Article </b> <br/>
                    <b> ID : </b> <span> {articleId} </span>
                    <input id="id-box" placeholder="User ID" onChange={ArticleDetail.editCreatedId}></input> <br/>
                    <input id="id-box" placeholder="Title" onChange={ArticleDetail.editTitle}></input> <br/>
                    <input id="id-box" placeholder="Category" onChange={ArticleDetail.editCategory}></input> <br/>
                    <textarea id="text-box" placeholder="Content" onChange={ArticleDetail.editContent}></textarea> <br/>
                    <button type="submit" id="btn-add" style={{textAlign: "right"}}
                            onClick={() => {window.location.href=`/${articleId}`;}}> Save </button>
                </div>
            </form>
        </div>
    )
}

export default ArticleEditForm();
