        return <div>
                    <span>{props.state}</span>
                    <br/>
               </div>
    });
    console.log(props);
    console.log(articles);
    
    
    let {Json} = props.state;

    return (
        <div>
            Board Data(Text â†’ JSON) :
            <div id="article">
                {(props.state)?.map(article => (
