import axios from "axios";
import _ from "lodash";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../App.css";
import { ifError } from "../func";

function LoginForm(){
    function LoginCheck(){
        const [user, setUser] = useState({ data : {} });
        const [check, setCheck] = useState(false);
        useEffect(() => {
            axios.get("/logininfo")
            .then((res) => {
                console.log(res.data);
                setUser(res?.data);
            })
            .then(() => {
                if(!_.isEmpty(user)){ setCheck(true); }
            })
            .catch((e) => {
                ifError(e);
            });
        });
        return check;
    }

    function Login(){
        axios.post('/userlogin').then((res) => {
            alert(res.data);
        })
        return LoginCheck();
    }
    return (
    <div id="div-box">
        <br/>
        <form onSubmit={() => { 
            const isLogin = Login();
            if(isLogin){ 
                sessionStorage.setItem("isLogin", isLogin);
                window.location.replace('/board'); 
            }}}>
            <h2 >Please Login</h2> <hr/>
            <input type="text" id="username" name="username" placeholder="ID (Email)" required autoFocus/><br/>
            <input type="password" id="password" name="password" placeholder="Password" required/><br/>
            <button id="btn-post" type="submit">Login</button>
            <Link to="/login/signup"><button id="btn-default">Sign up</button></Link>
        </form> 
    </div>
    )
}

export default LoginForm;
