import "../App.css";
import * as Validation from "../validation";
import axios from "axios";
import { useEffect, useState } from "react";
import MemberRegister from "./memberRegister";

function LoginForm(){

    const [id, setId] = useState("");
    const [pw, setPW] = useState("");

    const [loading, setLoading] = useState(false);
    const [msg, setMsg] = useState("");

    useEffect(()=> {
        if(msg) {
            setTimeout(() => {
                setMsg("");
                setLoading(false);
            }, 1500);
        }
    }, [msg])

    const login = (e) => {
        if(!id) {
            return alert("Input Id");
        }else if(!pw){
            return alert("Input PW");
        }else {
            axios.post("/login", {
                data : {
                    id: id,
                    password: pw
                }
            }).then((res) => {
                console.log(res.data);
                switch(res.data.code){
                    case 200:
                        console.log("login");
                        console.log(res.data.userInfo);
                        setMsg("");
                        break;
                    case 400:
                        setMsg("ID, PW is Empty");
                        break;
                    case 401:
                        setMsg("Not existing ID");
                        break;
                    case 402:
                        setMsg("Wrong Password");
                        break;
                    default:
                        break;
                }
            });
        }
        setLoading(true);
    }

    return (
        <div>
            <br/><br/>
            <form onSubmit={login}>
                <div id="div-box">
                    <b style={{textAlign: "center"}}> Login Page </b> <br/>
                    <b> ID : </b><input id="id-box" placeholder="ID"></input> <br/>
                    <b> PW : </b><input id="id-box" placeholder="Password"></input> <br/>
                    <button type="submit" id="btn-post" style={{textAlign: "right"}}
                            onClick={() => {window.location.href=`/`}}> Login </button>
                </div>
            </form>
            <b> Don't have id ? </b><button id="btn-default"> Sign up <MemberRegister/></button>
        </div>
    )
}

export default LoginForm;
