import React, {useState, useEffect} from "react";
import ArticleLists from "./articleList";

function ArticleDeatil({articleId}){
    const [articleDetail, setArticleDetail] = useState({
        data : {}
    });

    useEffect(()=> {
        async function fetchData() {
            const RES = await fetch(`/board/${articleId}`)
                            .then((res) => res.json());
    
            setArticleDetail({articleDetail:RES});
            console.log(articleDetail);
            return RES;
        }
        return setArticleDetail({articleDetail :fetchData()});
    }, [articleId, articleDetail])

    return(
        <li> Article Detail : 
            <b> ID : </b> <span> </span> <br/>
            <b> Title : </b> <span> </span> <br/>
            <b> Category : </b> <span> </span> <br/>
            <b> Content : </b> <span> </span> <br/>
            <b> Created By : </b> <span> </span> <br/>
            <b> Created At : </b> <span> </span> <br/>
            <b> Visit : </b> <span> </span> <br/>
            <li><b> Comment : </b> <span> 
                
                </span> <br/></li>
        </li>
    )
}

export default ArticleDeatil;
