import React, { Component } from "react";

class ArticleLists extends Component{
    constructor(props){
        super(props);
        this.state = {
            articleList: {
                id: '',
                title: '',
                content: '',
                created_id: '',
                created_at: '',
                category_name: '',
                visit_cnt: '',
                comment_cnt: ''
                // comment: {
                //     id: '',
                //     content: '',
                //     created_at: ''
                // }
            }
        }
    }

    componentDidMount() {
        this.callApi()
      }
    
      callApi = async () => {
        const RES = await (await fetch('/board')).text();
        this.setState({articleList: RES});
        console.log("Board Data" + this.state.articleList)
      }


      render(){
        return (
            <div>
                Board Data : <pre key={this.state.articleList} id={this.state.articleList.id} title={this.state.articleList.title}
                                 content={this.state.articleList.content} created_id={this.state.articleList.created_id} created_at={this.state.articleList.created_at}
                                 category_name={this.state.articleList.category_name} visit_cnt={this.state.articleList.visit_cnt}
                                 comment_cnt={this.state.articleList.comment_cnt}>Data</pre>
            </div>
                                //  comment={
                                //     [id=this.state.articleList.comment.id,
                                //     content=this.state.articleList.comment.content,
                                //     created_at=this.state.articleList.comment.created_at]}>Data</pre>
            // </div>
        )
      }
}

export default ArticleLists;
