import React, {Component} from "react";
import ArticleLists from "./articleList";
import './App.css';

class ArticleDetailView extends Component{
    constructor(props){
        super(props);
        this.state = {
                data:  {}
            }
    }

    componentDidMount() {
        this.callArticle();
    }


    callArticle = async () => {
        const RES = await fetch(`/board/${articleId}`)
                        .then((res) => res.json())
        return this.setState(this.state=RES);
    }

    render(){
        return(
            <div className="App">
                <ul id="ulNone"><ArticleLists ArticleLists={this.state} /></ul>
            </div>
        );
    }
}

export default ArticleDetailView;
