import React, { useEffect, useState } from "react";
import './App.css'

function Article({ article }) {
    return (
      <div>
        <b>ID : </b> <span>({article.id})</span> <br/>
        <b>Title : </b> <span>({article.data.title})</span> <br/>
        <b>Category : </b> <span>({article.data.category_name})</span> <br/>
        <b>Created By : </b> <span>({article.data.created_id})</span> <br/>
        <b>Created At : </b> <span>({article.data.created_at})</span> <br/>
        <b>Visit : </b> <span>({article.data.visit_cnt})</span> <br/>
        <b>Comment : </b> <span>({article.data.comment_cnt})</span> <br/>
      </div>
    );
}


function ArticleLists(props){
    const [articleList, setArticleList] = useState({
        data : [{
            id: '',
            title: '',
            created_id: '',
            created_at: '',
            category_name: '',
            visit_cnt: '',
            comment_cnt: ''
        }]
    });
    
    useEffect(()=> {
        setArticleList(props.ArticleLists);
        // console.log(articleList)
    },[props.ArticleLists, articleList])
    
    const articles = props.data?.map(article => {
        let {Json} = articles.article;
        // console.log({Json});
        return <div>
                    <span>{Json.article}</span>
                    <br/>
               </div>
    });
    // console.log(props);
    // console.log(props.ArticleLists);
    
    
    // let {Json} = props.state;

    return (
        <div>
            Board Data(Text â†’ JSON) :
            <Article list={props.ArticleLists}/>
            {/* <div id="article">
                { props.data?.map(article => (
                    <Article list={article} /> 
                ))}
            </div> */}
        </div>
    )
}

export default ArticleLists;
