import { useState } from "react";
import { split } from "lodash";
import { Link } from "react-router-dom";
import { Fetching, username } from "../func";

function MyPage(){
    const [visible, setVisible] = useState(false);
    const user = Fetching("user", username());

    if(!user) { return <div> Loading ... </div> }
    else {
        return (
            <div id="div-box">
                <h1>MY PAGE</h1><hr/>
                <div style={{textAlign: "left", border: "1px solid black", margin: "20px"}}>
                    <b> User Code : </b><span> {user.code} </span><br/>
                    <b> User Name : </b><span> {user.username} </span><br/>
                    {/* <b> PassWord : </b><span> {user.password} </span><br/> */}
                    <b> ROLE : </b><span> {split(user?.authorities[0]?.authority, "_")[1]} </span><br/>
                    <button id="btn-post" style={{position: "absolute", top: "115px", right: "30px"}}
                            onClick={() => setVisible(!visible)}> {visible ? "Cancel" : "Change PW"} </button>
                    {visible && <PasswordEditForm />}
                        {/* {visible && <CommentEditForm data={data} />} 
                        <><br/><br/>
                        <form onSubmit={editComment}>
                            <div id="div-box" style={{textAlign: "left"}}>
                                <b style={{textAlign: "center"}}> Edit comment </b> <br/>
                                <input  placeholder={data?.user_id} readOnly></input> <br/>
                                <input  placeholder={data?.content} onChange={editcontent}></input> <br/>
                                <button type="submit" id="btn-post" style={{textAlign: "right"}}> Save </button>
                            </div>
                        </form></> */}
                </div>
                <Link to={`/`} id="none"> <button id="btn-default"> Home </button></Link>
                <Link to={`/board`} id="none"> <button id="btn-default"> Board </button></Link>
            </div>
        )
    }
}

function PasswordEditForm() {
    return (
        <div>
            <br/>
            <form action="/user" method="put">
                <b> Change password </b><br/>
                <input placeholder="New Password"></input><br/>
                <input placeholder="Check Password"></input>
            </form>
        </div>
    )
}

export default MyPage;
